	public int saveAddress(DeliveryAddress deliveryAddress)
	{

		Connection conn = null;
		Statement stmt = null;
		int rowIdOfInsertedRow = -1;

		String insertItemCategory = "INSERT INTO "
				+ DeliveryAddressContract.TABLE_NAME
				+ "("  
				+ DeliveryAddressContract.END_USER_ID + ","
				+ DeliveryAddressContract.PINCODE + ","
				+ DeliveryAddressContract.PHONE_NUMBER + ","
				+ DeliveryAddressContract.NAME + ","
				+ DeliveryAddressContract.CITY + ","
				+ DeliveryAddressContract.DELIVERY_ADDRESS + ","
				+ DeliveryAddressContract.LANDMARK + ""
				+ " ) VALUES ( "
				+ "" + deliveryAddress.getEndUserID()	+ ","
				+ "" + deliveryAddress.getPincode() + ","
				+ "" + deliveryAddress.getPhoneNumber() + ","
				+ "'" + deliveryAddress.getName() + "',"
				+ "'" + deliveryAddress.getCity() + "',"
				+ "'" + deliveryAddress.getDeliveryAddress() + "',"
				+ "'" + deliveryAddress.getLandmark() + "'"
				+ ")";
		
		try {
			
			conn = DriverManager.getConnection(JDBCContract.CURRENT_CONNECTION_URL,
					JDBCContract.CURRENT_USERNAME,JDBCContract.CURRENT_PASSWORD);
			
			stmt = conn.createStatement();
			
			rowIdOfInsertedRow = stmt.executeUpdate(insertItemCategory,Statement.RETURN_GENERATED_KEYS);
			
			ResultSet rs = stmt.getGeneratedKeys();

			if(rs.next())
			{
				rowIdOfInsertedRow = rs.getInt(1);
			}
			
			
			
			System.out.println("Key autogenerated SaveDistributor: " + rowIdOfInsertedRow);
			
			
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		finally
		{
			
			try {
			
				if(stmt!=null)
				{stmt.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			
			try {
				
				if(conn!=null)
				{conn.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}

		
		return rowIdOfInsertedRow;
	}
	

	public int updateAddress(DeliveryAddress address)
	{	
		String updateStatement = "UPDATE " + DeliveryAddressContract.TABLE_NAME
				+ " SET "
				+ DeliveryAddressContract.END_USER_ID + " = "
				+ "" + address.getEndUserID() + ""
				+ ","
				+ DeliveryAddressContract.LANDMARK + " = "
				+ "'" + address.getLandmark() + "'"
				+ ","
				+ DeliveryAddressContract.DELIVERY_ADDRESS + " = "
				+ "'" + address.getDeliveryAddress() + "'"
				+ ","
				+ DeliveryAddressContract.CITY + " = "
				+ "'" + address.getCity() + "'"
				+ ","
				+ DeliveryAddressContract.PHONE_NUMBER + " = "
				+ "" + address.getPhoneNumber() + ""
				+ ","
				+ DeliveryAddressContract.PINCODE + " = "
				+ "" + address.getPincode() + ""
				+ ","
				+ DeliveryAddressContract.NAME + " = "
				+ "'" + address.getName() + "'"

				+ " WHERE " + DeliveryAddressContract.ID + " = "
				+ address.getId();
		
		Connection conn = null;
		Statement stmt = null;
		int updatedRows = -1;
		
		try {
			
			conn = DriverManager.getConnection(JDBCContract.CURRENT_CONNECTION_URL,
					JDBCContract.CURRENT_USERNAME,JDBCContract.CURRENT_PASSWORD);
			
			stmt = conn.createStatement();
			
			updatedRows = stmt.executeUpdate(updateStatement);
			
			
			System.out.println("Total rows updated: " + updatedRows);	
			
			//conn.close();
			
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		finally
		
		{
			
			try {
			
				if(stmt!=null)
				{stmt.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			
			try {
				
				if(conn!=null)
				{conn.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}

		return updatedRows;
		
	}
	

