	public int saveConfig(ServiceConfiguration serviceConfiguration)
	{

		Connection conn = null;
		Statement stmt = null;
		int rowIdOfInsertedRow = -1;

		String insertStatement = "INSERT INTO "
				+ ServiceConfigurationContract.TABLE_NAME
				+ "("
				+ ServiceConfigurationContract.NAME + ","
				+ ServiceConfigurationContract.SERVICE_ID + ""
				+ " ) VALUES ( "
				+ "" + serviceConfiguration.getName()	+ ","
				+ "" + serviceConfiguration.getServiceID() + ""
				+ ")";
		
		try {
			
			conn = DriverManager.getConnection(JDBCContract.CURRENT_CONNECTION_URL,
					JDBCContract.CURRENT_USERNAME,JDBCContract.CURRENT_PASSWORD);
			
			stmt = conn.createStatement();
			
			rowIdOfInsertedRow = stmt.executeUpdate(insertStatement,Statement.RETURN_GENERATED_KEYS);
			
			ResultSet rs = stmt.getGeneratedKeys();

			if(rs.next())
			{
				rowIdOfInsertedRow = rs.getInt(1);
			}
			
			
			
			System.out.println("Key autogenerated Save ServiceConfig: " + rowIdOfInsertedRow);
			
			
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		finally
		{
			
			try {
			
				if(stmt!=null)
				{stmt.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			
			try {
				
				if(conn!=null)
				{conn.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}

		
		return rowIdOfInsertedRow;
	}
	

	public int updateServiceConfig(ServiceConfiguration serviceConfiguration)
	{	
		String updateStatement = "UPDATE " + ServiceConfigurationContract.TABLE_NAME
				+ " SET "
				+ ServiceConfigurationContract.NAME + " = " + "'" + serviceConfiguration.getName() + "'" + ","
				+ "" + ServiceConfigurationContract.SERVICE_ID + " = " + "" + serviceConfiguration.getServiceID() + ""
				+ " WHERE "
				+ ServiceConfigurationContract.CONFIGURATION_ID + " = " + serviceConfiguration.getConfigurationID();


		Connection conn = null;
		Statement stmt = null;
		int updatedRows = -1;
		
		try {
			
			conn = DriverManager.getConnection(JDBCContract.CURRENT_CONNECTION_URL,
					JDBCContract.CURRENT_USERNAME,JDBCContract.CURRENT_PASSWORD);
			
			stmt = conn.createStatement();
			
			updatedRows = stmt.executeUpdate(updateStatement);
			
			
			System.out.println("Total rows updated: " + updatedRows);	
			
			//conn.close();
			
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		finally
		
		{
			
			try {
			
				if(stmt!=null)
				{stmt.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			
			try {
				
				if(conn!=null)
				{conn.close();}
			} catch (SQLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}

		return updatedRows;
		
	}
	

